// React
import React, { useState, useEffect } from "react";
import { ImageFullWork } from "./modules/ImageOnlyModules";
import {
  useSingleWorkFromModule,
  formatWorkInfoLine,
  formatWorkInfoLineRefactored,
} from "./modules/useWork";

function PageCollections({ page, base, allworks }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [fullScreenIndex, setFullScreenIndex] = useState(undefined);

  useEffect(() => {
    if (Object.keys(allworks).length === 0) setIsLoading(true);
    else {
      const title = page["Title"];
      base(title)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _collectionWorks = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let workRecord = allworks[workId];
            _collectionWorks.push(workRecord);
          });
          setCollectionWorks([..._collectionWorks]);
          setIsLoading(false);
        });
    }
  }, [allworks]);

  if (isLoading) return null;

  if (fullScreenIndex === undefined) {
    return (
      <div style={{ backgroundColor: "red" }}>
        <div
          style={{
            display: "flex",
            direction: "row",
            flexWrap: "wrap",
            maxWidth: "100%",
          }}
        >
          {collectionWorks.map((workRecord, i) => {
            return (
              <div
                style={{ minWidth: "200px", height: "auto" }}
                onClick={() => {
                  setFullScreenIndex(i);
                }}
              >
                <img
                  alt="artwork"
                  style={{ minWidth: "200px", height: "auto" }}
                  src={workRecord.fields["Work"][0].url}
                />
                {formatWorkInfoLineRefactored(workRecord)}
              </div>
            );
          })}
        </div>
      </div>
    );
  }

  let fullScreenWorkRecord = collectionWorks[fullScreenIndex];
  return (
    <div id={"page"}>
      <div id="scrollContent">
        <div className="pageModule imageFullModule">
          <img
            alt="artwork"
            className="workImage"
            style={{ width: "100%", height: "auto" }}
            src={fullScreenWorkRecord.fields["Work"][0].url}
          />
          {formatWorkInfoLineRefactored(fullScreenWorkRecord)}
        </div>
      </div>

      <div
        onClick={() => {
          setFullScreenIndex(undefined);
        }}
        className={"upArrowOnHover"}
        style={{
          position: "absolute",
          top: "0px",
          left: "0px",
          right: "0px",
          height: "50%",
        }}
      ></div>

      <div
        onClick={() => {
          setFullScreenIndex(
            (fullScreenIndex - 1 + collectionWorks.length) %
              collectionWorks.length
          );
        }}
        className={"leftArrowOnHover"}
        style={{
          position: "absolute",
          top: "50%",
          left: "0px",
          width: "50%",
          bottom: "0px",
        }}
      ></div>
      <div
        onClick={() => {
          setFullScreenIndex(
            (fullScreenIndex + 1 + collectionWorks.length) %
              collectionWorks.length
          );
        }}
        className={"rightArrowOnHover"}
        style={{
          position: "absolute",
          top: "50%",
          right: "0px",
          width: "50%",
          bottom: "0px",
        }}
      ></div>
    </div>
  );
}

export default PageCollections;
