// React
import React, { useState, useEffect } from "react";

function CVPage({ page, base }) {
  const [bio, setBio] = useState("");
  const [bioImage, setBioImage] = useState([]);

  useEffect(() => {
    base("CV")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        const recordsByCategory = {};
        records.forEach(function (record) {
          const category = record.get("Category")[0];
          if (recordsByCategory[category] === undefined) {
            recordsByCategory[category] = [record];
          } else {
            recordsByCategory[category] =
              recordsByCategory[category].append(record);
          }
        });
      });
    console.log(recordsByCategory);
  }, [page.pageTitle]);

  return (
    <div id={"page"}>
      <div id="scrollContent">
        <div
          className="cvContainer"
          style={{
            flexDirection: "row",
            gap: 50,
            alignItems: "flex-start",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              flex: 0.5,
              flexDirection: "column",
              width: "100%",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <img
              alt="studio portrait"
              className="workImage"
              style={{ width: "70%", maxHeight: 400, height: "auto" }}
              src={bioImage[0] ? bioImage[0].url : ""}
            />
          </div>
          <div
            className="imageWithTextModuleText"
            style={{
              display: "flex",
              fontSize: 18,
              flex: 1,
              flexDirection: "column",
            }}
          >
            {bio && (
              <div className="workText" style={{ whiteSpace: "pre-wrap" }}>
                {bio}
                <div>
                  <a
                    target="_blank"
                    rel="noreferrer"
                    href="https://www.instagram.com/sammytthebrave/"
                  >
                    @sammytthebrave
                  </a>
                </div>
                <div>
                  <a
                    target="_blank"
                    rel="noreferrer"
                    href="https://airtable.com/shr1xUUOkb0Gcs3Uv"
                  >
                    Join my mailing list
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default CVPage;

