// React
import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

function MenuInstallations(props) {
  const { installations } = props;

  return (
    <div id="menuContainer">
      <div
        style={{
          display: "flex",
          flex: 1,
          alignItems: "space-between",
          flexDirection: "row",
          gap: 40,
        }}
      >
        {installations.map((installation, index) => {
          if (installation.showInMenu) {
            return (
              <Link
                style={{ width: "300px", height: "200px" }}
                to={installation.route}
                key={`menu-${installation.route}`}
              >
                <div style={{ position: "relative" }}>
                  <img
                    alt="install preview"
                    style={{
                      position: "absolute",
                      top: 0,
                      bottom: 0,
                      left: 0,
                      right: 0,
                      objectFit: "cover",
                    }}
                    src={installation.imageLink}
                  />
                  <div
                    style={{
                      display: "flex",
                      flex: 1,
                      alignItems: "space-between",
                      flexDirection: "column",
                    }}
                  >
                    <span className="menuOptionTitle">
                      {installation.title}
                    </span>
                    <div>
                      {installation.location && (
                        <>
                          <br />
                          <span className="menuOptionSubtitle">
                            {installation.location}
                          </span>
                        </>
                      )}
                      {installation.dates && (
                        <>
                          <br />
                          <span className="menuOptionSubtitle">
                            {installation.dates}
                          </span>
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </Link>
            );
          }
        })}
      </div>
    </div>
  );
}

MenuInstallations.propTypes = {
  menuItems: PropTypes.array,
};

export default MenuInstallations;
