// React
import React, { useState, useEffect } from "react";

function PageAbout({ base }) {
  const [bio, setBio] = useState("");
  const [bioImage, setBioImage] = useState([]);
  const [showCVLink, setShowCVLink] = useState(false);

  useEffect(() => {
    base("About")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _bio;
        let _bioImage;
        records.forEach(function (record) {
          _bioImage = record.get("Bio Image");
          _bio = record.get("Bio");
          setBio(_bio);
          setBioImage(_bioImage);
          setShowCVLink(record.get("Show CV"));
        });
      });
  }, [base]);

  return (
    <div style={{ marginTop: "64px" }}>
      <div
        style={{
          padding: "64px",
          display: "flex",
          flexDirection: "row",
          gap: 64,
          alignItems: "flex-start",
          justifyContent: "flex-start",
        }}
      >
        <div
          style={{
            display: "flex",
            flex: 0.3,
            flexDirection: "column",
            width: "100%",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <img
            alt="Studio portrait"
            style={{ width: "100%", height: "auto" }}
            src={bioImage[0] ? bioImage[0].url : ""}
          />
        </div>
        <div
          className="imageWithTextModuleText"
          style={{
            display: "flex",
            fontSize: 18,
            flex: 1,
            flexDirection: "column",
          }}
        >
          {bio && (
            <div className="workText" style={{ whiteSpace: "pre-wrap" }}>
              {bio}
              {showCVLink && (
                <div>
                  <a href="/#/CV" style={{ textDecoration: "none" }}>
                    üìÑ CV
                  </a>
                </div>
              )}
              <div>
                <a
                  target="_blank"
                  rel="noreferrer"
                  href="https://www.instagram.com/sammytthebrave/"
                  style={{ textDecoration: "none" }}
                >
                  üîó Instagram
                </a>
              </div>
              <div>
                <a
                  target="_blank"
                  rel="noreferrer"
                  href="https://airtable.com/appFu4bKxcQWPoN69/shrU6lsf5P4fqJlpj"
                  style={{ textDecoration: "none" }}
                >
                  ‚úâÔ∏è Join Mailing List
                </a>
              </div>
              <div>‚úâÔ∏è srticknor@gmail.com</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default PageAbout;
