// React
import React, { useState, useEffect } from "react";

function CVPage({ page, base }) {
  const [recordsByCategory, setRecordsByCategory] = useState({});

  useEffect(() => {
    base("CV")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        const _recordsByCategory = {};
        console.log(records);
        records.forEach(function (record) {
          console.log(record);
          const category = record.get("Category")[0];
          if (_recordsByCategory[category] === undefined) {
            _recordsByCategory[category] = [record];
          } else {
            _recordsByCategory[category] = _recordsByCategory[category] +=
              record;
          }
        });
        recordsByCategory.set(_recordsByCategory);
      });
  }, [page.pageTitle]);

  console.log(recordsByCategory);
  return (
    <div id={"page"}>
      <div id="scrollContent">
        <div
          className="pageContainer"
          style={{
            flexDirection: "row",
            gap: 50,
            alignItems: "flex-start",
            justifyContent: "center",
          }}
        >
          {Object.keys(recordsByCategory).map((category, index) => {
            return (
              <div className="" key={`${category}_${index}`}>
                {category}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default CVPage;

