// React
import React, { useState, useEffect } from "react";
import { ImageFullModule } from "./modules/ImageOnlyModules";

function PageCollections({ page, base, allworks }) {
  const [works, setWorks] = useState(undefined);

  useEffect(() => {
    if (document.querySelector("#scrollContent")) {
      document.querySelector("#page").scrollTop = 0;
    }

    const title = page["Title"];
    console.log("TITLE");
    console.log(title);

    base(title)
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _works = [];
        records.forEach(function (record) {
          let workId = record.get("Work")[0];
          let work = allworks[workId];
          _works.push(work);
        });
        setWorks(_works);
      });
  }, [page.pageTitle]);

  if (works === undefined) return null;

  return (
    <div id={"page"}>
      <div id="scrollContent">
        {works.map((work, index) => {
          return (
            <div>{work.fields["Title"]}</div>
            // <ImageFullModule
            //   key={module.moduleTitle}
            //   module={module}
            //   base={base}
            // />
          );
        })}
      </div>
    </div>
  );
}

export default PageCollections;
