// React
import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home({ splashImages, collections, dateCollections, installations }) {
  const [toggle, setToggle] = useState("collection");

  return (
    <div style={{ zIndex: 1 }}>
      {/* Splash Image on Right */}
      {splashImages && splashImages[0] && (
        <div
          className="mobileHide"
          style={{
            position: "fixed",
            top: "0px",
            right: "0px",
            bottom: "0px",
            width: "40vw",
            zIndex: 200,
          }}
        >
          <img
            alt="A splashy introduction to the artwork of Sam Ticknor"
            src={splashImages[0].url}
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              objectPosition: "top center",
            }}
          ></img>
        </div>
      )}

      {/* Menus on Left */}
      <div style={{ width: "60vw" }} className="mobileFullWidth">
        <div
          className="mobilePadding"
          style={{
            marginTop: "64px",
            gap: "64px",
            padding: "64px",
            display: "flex",
            flex: 1,
            flexDirection: "column",
          }}
        >
          {/* Collections Menu */}
          <div
            style={{
              display: "flex",
              flexDirection: "column",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                fontWeight: 600,
              }}
            >
              Works by:
              <div
                onClick={() => {
                  setToggle("collection");
                }}
                className="clickable"
                style={{
                  margin: "0px 5px",
                  textDecoration:
                    toggle === "collection" ? "underline" : "none",
                }}
              >
                Collection
              </div>
              /
              <div
                onClick={() => {
                  setToggle("date");
                }}
                className="clickable"
                style={{
                  margin: "0px 5px",
                  textDecoration: toggle === "date" ? "underline" : "none",
                }}
              >
                Date
              </div>
            </div>
            <div
              style={{
                borderLeft: "1px solid black",
                padding: "0px 32px",
                marginTop: "32px",
                display: "flex",
                flexDirection: "column",
                gap: "16px",
              }}
            >
              {toggle === "collection" &&
                collections.map((collection, index) => {
                  if (!collection.showInMenu) return null;
                  return (
                    <Link
                      to={collection.route}
                      key={`menu-${collection.route}`}
                      style={{
                        fontWeight: 600,
                        textDecoration: "none",
                        color: "black",
                      }}
                      className="clickable"
                    >
                      <>
                        {collection.title}
                        {collection.years && (
                          <span style={{ opacity: 0.6, fontWeight: 500 }}>
                            {" — "}
                            {collection.years}
                          </span>
                        )}
                      </>
                    </Link>
                  );
                })}
              {toggle === "date" &&
                Object.keys(dateCollections).map((dateCollectionID, index) => {
                  return (
                    <Link
                      to={dateCollectionID}
                      key={`menu-${dateCollectionID}`}
                      style={{
                        textDecoration: "none",
                        fontWeight: 600,
                        color: "black",
                      }}
                      className="clickable"
                    >
                      {dateCollectionID}
                    </Link>
                  );
                })}
            </div>
          </div>

          {/* Installations Menu */}
          <div>
            <span
              style={{
                display: "flex",
                flexDirection: "row",
                fontWeight: 600,
                marginBottom: "32px",
              }}
            >
              Installations:
            </span>
            <div
              style={{
                display: "flex",
                flex: 1,
                alignItems: "space-between",
                flexDirection: "row",
                gap: 32,
                flexWrap: "wrap",
                maxWidth: "786px",
              }}
            >
              {installations.map((installation) => {
                if (installation.showInMenu) {
                  return (
                    <Link
                      style={{
                        width: "230px",
                        aspectRatio: "3/2",
                        position: "relative",
                        textDecoration: "none",
                      }}
                      to={installation.route}
                      key={`menu-${installation.route}`}
                    >
                      <img
                        alt="Installation Preview"
                        style={{
                          position: "absolute",
                          top: "0px",
                          bottom: "0px",
                          left: "0px",
                          right: "0px",
                          width: "100%",
                          height: "100%",
                          objectFit: "cover",
                        }}
                        src={installation.imageLink}
                      />
                      <div
                        style={{
                          position: "absolute",
                          top: "0px",
                          bottom: "0px",
                          left: "0px",
                          right: "0px",
                          width: "100%",
                          height: "100%",
                          background:
                            "linear-gradient(to right, rgba(0, 0, 0, .8), rgba(0, 0, 0, .1))",
                        }}
                      />
                      <div
                        style={{
                          display: "flex",
                          flex: 1,
                          flexDirection: "column",
                          height: "100%",
                        }}
                        className="clickable"
                      >
                        <div
                          style={{
                            color: "white",
                            zIndex: 2,
                            padding: "16px 16px 16px 16px",
                            lineHeight: 1.5,
                            display: "flex",
                            flex: 1,
                            flexDirection: "column",
                            justifyContent: "space-between",
                            gap: "8px",
                          }}
                        >
                          <div style={{ fontWeight: 600 }}>
                            {installation.title}
                          </div>
                          <div>
                            {installation.location && (
                              <div>{installation.location}</div>
                            )}
                            {installation.dates && (
                              <div>{installation.dates}</div>
                            )}
                          </div>
                        </div>
                      </div>
                    </Link>
                  );
                }
                return null;
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
