// React
import React, { useState } from "react";
import MenuCollections from "./MenuCollections";
import MenuInstallations from "./MenuInstallations";

function Home({
  splashImages,
  collections,
  dateCollections,
  installations,
  isLoading,
}) {
  const [toggle, setToggle] = useState("collection");

  return (
    <div
      style={{
        display: "flex",
        position: "absolute",
        top: "64px",
        left: "0px",
        right: "0px",
        bottom: "64px",
        padding: "32px",
        overflow: "hidden",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "1600px",
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
          gap: "200px",
        }}
      >
        <div>
          {/* Collections Menu */}
          <div>
            <div style={{ display: "flex", flexDirection: "row" }}>
              Works by:
              <div
                onClick={() => {
                  setToggle("collection");
                }}
              >
                Collection
              </div>
              /
              <div
                onClick={() => {
                  setToggle("date");
                }}
              >
                Date
              </div>
            </div>
            {toggle === "collection" &&
              collections.map((collection, index) => {
                if (!collection.showInMenu) return null;
                return (
                  <Link
                    className="menuOption clickable"
                    to={collection.route}
                    key={`menu-${collection.route}`}
                  >
                    <>
                      <span className="menuOptionTitle">
                        {collection.title}
                      </span>
                      {collection.years && (
                        <span className="menuOptionSubtitle">
                          {" — "}
                          {collection.years}
                        </span>
                      )}
                    </>
                  </Link>
                );
              })}
            {toggle === "date" &&
              Object.keys(dateCollections).map((dateCollectionID, index) => {
                return (
                  <Link
                    className="menuOption clickable"
                    to={dateCollectionID}
                    key={`menu-${dateCollectionID}`}
                  >
                    <>
                      <span className="menuOptionTitle">
                        {dateCollectionID}
                      </span>
                    </>
                  </Link>
                );
              })}
          </div>

          {/* Installations Menu */}
          <MenuInstallations
            installations={installations}
            isLoading={isLoading}
          />
        </div>
        {splashImages && splashImages[0] && (
          <img
            src={splashImages[0].url}
            style={{
              flex: 1,
              objectFit: "cover",
            }}
          ></img>
        )}
      </div>
    </div>
  );
}

export default Home;
