// React
import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home({
  splashImages,
  collections,
  dateCollections,
  installations,
  isLoading,
}) {
  const [toggle, setToggle] = useState("collection");

  return (
    <div>
      {/* Splash Image on Right */}
      {splashImages && splashImages[0] && (
        <div
          style={{
            position: "fixed",
            top: "0px",
            right: "0px",
            bottom: "0px",
            width: "40vw",
            zIndex: 200,
          }}
        >
          <img
            alt="A splashy introduction to the artwork of Sam Ticknor"
            src={splashImages[0].url}
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              objectPosition: "top center",
            }}
          ></img>
        </div>
      )}

      {/* Menus on Left */}
      <div style={{ width: "60vw", background: "pink" }}>
        <div
          style={{
            marginTop: "64px",
            gap: "64px",
            padding: "64px",
            display: "flex",
            flex: 1,
            flexDirection: "column",
          }}
        >
          {/* Collections Menu */}
          <div
            style={{
              display: "flex",
              flexDirection: "column",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                fontSize: "16px",
                fontWeight: 700,
              }}
            >
              Works by:
              <div
                onClick={() => {
                  setToggle("collection");
                }}
                className="clickable"
                style={{
                  margin: "0px 5px",
                  textDecoration:
                    toggle === "collection" ? "underline" : "none",
                }}
              >
                Collection
              </div>
              /
              <div
                onClick={() => {
                  setToggle("date");
                }}
                className="clickable"
                style={{
                  margin: "0px 5px",
                  textDecoration: toggle === "date" ? "underline" : "none",
                }}
              >
                Date
              </div>
            </div>
            <div
              style={{
                borderLeft: "1px solid black",
                padding: "0px 24px",
                marginTop: "24px",
                display: "flex",
                flexDirection: "column",
                gap: "16px",
              }}
            >
              {toggle === "collection" &&
                collections.map((collection, index) => {
                  if (!collection.showInMenu) return null;
                  return (
                    <Link
                      to={collection.route}
                      key={`menu-${collection.route}`}
                      style={{
                        fontSize: "16px",
                        textDecoration: "none",
                        color: "black",
                      }}
                      className="clickable"
                    >
                      <>
                        {collection.title}
                        {collection.years && (
                          <span style={{ opacity: 0.6 }}>
                            {" â€” "}
                            {collection.years}
                          </span>
                        )}
                      </>
                    </Link>
                  );
                })}
              {toggle === "date" &&
                Object.keys(dateCollections).map((dateCollectionID, index) => {
                  return (
                    <Link
                      to={dateCollectionID}
                      key={`menu-${dateCollectionID}`}
                      style={{
                        fontSize: "16px",
                        textDecoration: "none",
                        color: "black",
                      }}
                      className="clickable"
                    >
                      {dateCollectionID}
                    </Link>
                  );
                })}
            </div>
          </div>

          {/* Installations Menu */}
          <div>
            <span
              style={{
                display: "flex",
                flexDirection: "row",
                fontSize: "16px",
                fontWeight: 700,
                marginBottom: "24px",
              }}
            >
              Installations:
            </span>
            <div
              style={{
                display: "flex",
                flex: 1,
                alignItems: "space-between",
                flexDirection: "row",
                gap: 40,
                flexWrap: "wrap",
              }}
            >
              {installations.map((installation, index) => {
                if (installation.showInMenu) {
                  return (
                    <Link
                      style={{
                        width: "200px",
                        aspectRatio: "3/2",
                        position: "relative",
                        textDecoration: "none",
                      }}
                      to={installation.route}
                      key={`menu-${installation.route}`}
                    >
                      <img
                        alt="install preview"
                        style={{
                          position: "absolute",
                          top: "0px",
                          bottom: "0px",
                          left: "0px",
                          right: "0px",
                          width: "100%",
                          height: "100%",
                          objectFit: "cover",
                        }}
                        src={installation.imageLink}
                      />
                      <div
                        style={{
                          position: "absolute",
                          top: "0px",
                          bottom: "0px",
                          left: "0px",
                          right: "0px",
                          width: "100%",
                          height: "100%",
                          background:
                            "linear-gradient(to right, rgba(0, 0, 0, .8), rgba(0, 0, 0, 0))",
                        }}
                      />
                      <div
                        style={{
                          display: "flex",
                          flex: 1,
                          alignItems: "space-between",
                          justifyContent: "space-between",
                          flexDirection: "column",
                          height: "100%",
                        }}
                      >
                        <div
                          style={{
                            color: "white",
                            zIndex: 2,
                            padding: "16px 16px 4px 16px",
                          }}
                        >
                          <span style={{ fontWeight: 600 }}>
                            {" "}
                            {installation.title}
                          </span>
                          {installation.location && (
                            <>
                              <br />
                              <span
                                style={{ fontSize: "14px", lineHeight: "14px" }}
                              >
                                at {installation.location}
                              </span>
                            </>
                          )}
                          {installation.dates && (
                            <>
                              <br />
                              <span
                                style={{ fontSize: "14px", lineHeight: "14px" }}
                              >
                                {installation.dates}
                              </span>
                            </>
                          )}
                        </div>
                      </div>
                    </Link>
                  );
                }
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
