// React
import React, { useState, useEffect } from "react";
import { formatWorkInfoLineStacked } from "./modules/formatWorkDetails";

function PageCollections({ collection, base, allWorksById }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [fullScreenIndex, setFullScreenIndex] = useState(undefined);

  useEffect(() => {
    if (Object.keys(allWorksById).length === 0) setIsLoading(true);
    else {
      const tableName = collection.route;
      base(tableName)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _collectionWorks = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let workRecord = allWorksById[workId];
            _collectionWorks.push(workRecord);
          });
          setCollectionWorks([..._collectionWorks]);
          setIsLoading(false);
        });
    }
  }, [allWorksById]);

  if (isLoading) return null;

  let imageGrid;
  let fullScreenLightbox;
  let imageDetails;

  // Show all thumbnails
  if (fullScreenIndex === undefined) {
    imageGrid = (
      <div>
        <div
          style={{
            display: "flex",
            direction: "row",
            flexWrap: "wrap",
            maxWidth: "100%",
            gap: "32px",
          }}
        >
          {collectionWorks.map((work, i) => {
            return (
              <div
                style={{ width: "200px", height: "200px", overflow: "hidden" }}
                onClick={() => {
                  setFullScreenIndex(i);
                }}
              >
                <img
                  alt="artwork"
                  style={{
                    width: "300px",
                    height: "300px",
                    marginLeft: "-50px",
                    marginTop: "-50px",
                    objectFit: "cover",
                  }}
                  src={work.imageLink}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  } else {
    // Show full screen record
    let fullScreenWorkRecord = collectionWorks[fullScreenIndex];

    fullScreenLightbox = (
      <div
        style={{
          position: "fixed",
          top: "0px",
          left: "0px",
          right: "0px",
          bottom: "0px",
          background: "rgba(0, 0, 0, .8)",
          display: "flex",
          flexDirection: "row",
          alignItems: "flex-end",
          justifyContent: "center",
          gap: "64px",
          flex: 1,
          padding: "64px",
          zIndex: 1000,
        }}
        className={"rightArrowOnHover"}
        onClick={() => {
          setFullScreenIndex(
            (fullScreenIndex + 1 + collectionWorks.length) %
              collectionWorks.length
          );
        }}
      >
        <img
          alt="artwork"
          style={{
            width: "auto",
            height: "100%",
            maxHeight: "100%",
            maxWidth: "100%",
            objectFit: "contain",
          }}
          src={fullScreenWorkRecord.imageLink}
        />
        <div
          style={{
            background:
              "linear-gradient(90deg,rgba(255, 255, 240, .8) 0%,rgba(246, 254, 255, .8) 100%)",
            textAlign: "left",
            padding: "32px",
          }}
        >
          {formatWorkInfoLineStacked(fullScreenWorkRecord)}
        </div>
      </div>
    );

    // Conditionally generate image details
    // and prev / next buttons
    // imageDetails = (
    //   <div>
    //     {formatWorkInfoLineStacked(fullScreenWorkRecord)}

    //     <div>
    //       <div
    //         onClick={() => {
    //           setFullScreenIndex(
    //             (fullScreenIndex - 1 + collectionWorks.length) %
    //               collectionWorks.length
    //           );
    //         }}
    //         style={{ fontWeight: 600, userSelect: "none" }}
    //         className={"leftArrowOnHover"}
    //       >
    //         prev
    //       </div>
    //       <div style={{ marginLeft: 8, marginRight: 8 }}>/</div>
    //       <div
    //         onClick={() => {
    //           setFullScreenIndex(
    //             (fullScreenIndex + 1 + collectionWorks.length) %
    //               collectionWorks.length
    //           );
    //         }}
    //         style={{ fontWeight: 600, userSelect: "none" }}
    //         className={"rightArrowOnHover"}
    //       >
    //         next
    //       </div>
    //     </div>
    //   </div>
    // );
  }

  return (
    <div style={{ marginTop: "64px" }}>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          padding: "64px",
          flex: 1,
          gap: "64px",
        }}
        className="mobileStack mobilePadding mobileGap"
      >
        <div
          style={{
            flex: 1,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "space-between",
            flexDirection: "column",
          }}
        >
          <div>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>
              {collection.title}
            </div>
            {collection.collectionStatement && (
              <div
                style={{
                  borderLeft: "1px solid black",
                  padding: 24,
                  whiteSpace: "pre-wrap",
                }}
              >
                {collection.collectionStatement}
              </div>
            )}
          </div>
        </div>
        <div
          style={{
            height: "100%",
            flex: 2,
            display: "flex",
            overflow: "auto",
            WebkitOverflowScrolling: "touch",
          }}
        >
          <div
            style={{
              display: "flex",
              direction: "row",
              flexWrap: "wrap",
              maxWidth: "100%",
              gap: "32px",
            }}
          >
            {collectionWorks.map((work, i) => {
              return (
                <div
                  style={{
                    width: "200px",
                    height: "200px",
                    overflow: "hidden",
                  }}
                  onClick={() => {
                    setFullScreenIndex(i);
                  }}
                >
                  <img
                    alt="artwork"
                    style={{
                      width: "300px",
                      height: "300px",
                      marginLeft: "-50px",
                      marginTop: "-50px",
                      objectFit: "cover",
                    }}
                    src={work.imageLink}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {fullScreenLightbox}
    </div>
  );
}

export default PageCollections;
