// React
import React, { useState, useEffect } from "react";
import { formatWorkInfoLineRefactored } from "./modules/useWork";

function PageCollections({ collection, base, allWorksById }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [fullScreenIndex, setFullScreenIndex] = useState(undefined);

  useEffect(() => {
    if (Object.keys(allWorksById).length === 0) setIsLoading(true);
    else {
      const tableName = collection.tableName;
      base(tableName)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _collectionWorks = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let workRecord = allWorksById[workId];
            _collectionWorks.push(workRecord);
          });
          setCollectionWorks([..._collectionWorks]);
          setIsLoading(false);
        });
    }
  }, [allWorksById]);

  if (isLoading) return null;

  if (fullScreenIndex === undefined) {
    return (
      <div>
        <div
          style={{
            display: "flex",
            direction: "row",
            flexWrap: "wrap",
            maxWidth: "100%",
            gap: "50px",
          }}
        >
          {collectionWorks.map((work, i) => {
            return (
              <div
                style={{ width: "200px", height: "200px", overflow: "hidden" }}
                onClick={() => {
                  setFullScreenIndex(i);
                }}
              >
                <img
                  alt="artwork"
                  style={{
                    width: "300px",
                    height: "300px",
                    marginLeft: "-50px",
                    marginTop: "-50px",
                    objectFit: "cover",
                  }}
                  src={work.imageLink}
                />
                {formatWorkInfoLineRefactored(work)}
              </div>
            );
          })}
        </div>
      </div>
    );
  }

  let fullScreenWorkRecord = collectionWorks[fullScreenIndex];
  return (
    <div>
      <div>
        <div>
          <img
            alt="artwork"
            className="workImage"
            style={{ width: "100%", height: "auto" }}
            src={fullScreenWorkRecord.imageLink}
          />
          {formatWorkInfoLineRefactored(fullScreenWorkRecord)}
        </div>
      </div>

      <div
        onClick={() => {
          setFullScreenIndex(undefined);
        }}
        className={"upArrowOnHover"}
        style={{
          position: "absolute",
          top: "0px",
          left: "0px",
          right: "0px",
          height: "50%",
        }}
      ></div>

      <div
        onClick={() => {
          setFullScreenIndex(
            (fullScreenIndex - 1 + collectionWorks.length) %
              collectionWorks.length
          );
        }}
        className={"leftArrowOnHover"}
        style={{
          position: "absolute",
          top: "50%",
          left: "0px",
          width: "50%",
          bottom: "0px",
        }}
      ></div>
      <div
        onClick={() => {
          setFullScreenIndex(
            (fullScreenIndex + 1 + collectionWorks.length) %
              collectionWorks.length
          );
        }}
        className={"rightArrowOnHover"}
        style={{
          position: "absolute",
          top: "50%",
          right: "0px",
          width: "50%",
          bottom: "0px",
        }}
      ></div>
    </div>
  );
}

export default PageCollections;
