// React
import React, { useState, useEffect } from "react";
import { ImageFullWork } from "./modules/ImageOnlyModules";
import {
  useSingleWorkFromModule,
  formatWorkInfoLine,
  formatWorkInfoLineRefactored,
} from "./components/useWork";

function PageCollections({ page, base, allworks }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (Object.keys(allworks).length === 0) setIsLoading(true);
    else {
      const title = page["Title"];
      base(title)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _collectionWorks = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let workRecord = allworks[workId];
            _collectionWorks.push(workRecord);
          });
          setCollectionWorks([..._collectionWorks]);
          setIsLoading(false);
        });
    }
  }, [allworks]);

  if (isLoading) return null;

  return (
    <div id={"page"}>
      <div id="scrollContent">
        {collectionWorks.map((workRecord, index) => {
          console.log("MAPPED WORK");
          console.log(index);
          console.log(workRecord);
          return (
            <div className="pageModule imageFullModule">
              <img
                alt="artwork"
                className="workImage"
                style={{ width: "100%", height: "auto" }}
                src={workRecord.works[0].url}
              />
              {formatWorkInfoLineRefactored(workRecord)}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default PageCollections;
