// React
import React, { useState, useEffect } from "react";
import { Routes, HashRouter, Route } from "react-router-dom";

// Style
import { createGlobalStyle } from "styled-components";

// Helmet
import Helmet from "react-helmet";

// Splash
import SplashImage from "./splash.jpg";

// Screens
import Home from "./components/Home";
import Page from "./components/Page";
import PriceListPage from "./components/PriceListPage";
import InstagramEmbedPage from "./components/InstagramEmbedPage";
import Menu from "./components/Menu";
import CollectionsMenu from "./components/CollectionsMenu";

// Style
import "./App.css";
import AboutPage from "./components/AboutPage";
import CVPage from "./components/CVPage";

export default function App() {
  // const { location } = useLocation();

  // Site-Wide State
  const [siteTitle, setSiteTitle] = useState("Sam Ticknor");
  const [siteFavicon, setSiteFavicon] = useState("ðŸ”…");
  const [homeSplashImages, setHomeSplashImages] = useState([]);
  const [collections, setCollections] = useState([]);
  const [installations, setInstallations] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Airtable vars
  const [globalCss, setGlobalCss] = useState("");

  // AIRTABLE CONTENT
  var Airtable = require("airtable");
  Airtable.configure({
    endpointUrl: "https://api.airtable.com",
    apiKey:
      "pattHY0czY5YOotHV.e0784aa4aa945b3303da15e14c71b08f80cdf91802cc481c260c880ec890af04", // a read-only key
  });
  const base = Airtable.base("apptr77RaXLy74iuy");

  console.log(base);

  useEffect(() => {
    base("About")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _siteTitle;
        let _siteFavicon;
        let _homeSplashImages;
        records.forEach(function (record) {
          _siteTitle = record.get("Site Title");
          // _homeSplashImages = record.get("Splash Images");
          _siteFavicon = record.get("Emoji Favicon");
        });
        setSiteTitle(_siteTitle);
        setSiteFavicon(_siteFavicon);
        // setHomeSplashImages(_homeSplashImages);
      });

    base("Collections Index")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        console.log(records);
        if (err) {
          console.error(err);
          return;
        }
        const _collections = [];
        records.forEach((record) => {
          if (record.get("Show In Menu")) {
            _collections.push(record.fields);
          }
        });
        setCollections(_collections);
        setIsLoading(false);
      });

    base("Installations Index")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        console.log(records);
        if (err) {
          console.error(err);
          return;
        }
        const _installations = [];
        records.forEach((record) => {
          if (record.get("Show In Menu")) {
            _installations.push(record.fields);
          }
        });
        setInstallations(_installations);
        setIsLoading(false);
      });
  }, []);

  // STYLE CONFIG
  const GlobalStyles = createGlobalStyle`
    body {
      ${(props) => props.globalCss}
    }
  `;

  // MARKUP
  return (
    <HashRouter basename="/" key={window.location.pathname}>
      <Helmet>
        <title>{siteTitle}</title>
        <link
          rel="icon"
          href={`data:image/svg+xml,<svg xmlns=%22http://www.w3.org/2000/svg%22 viewBox=%220 0 100 100%22><text y=%22.9em%22 font-size=%2290%22>${siteFavicon}</text></svg>`}
          sizes="16x16"
        />
      </Helmet>
      <GlobalStyles globalCss={globalCss} />
      {/* MENU */}
      <CollectionMenu
        siteTitle={siteTitle}
        menuItems={[...collections, ...installations]}
        isLoading={isLoading}
      />
      <Routes key={window.location.pathname}>
        <Route
          path="/CV"
          element={<CVPage base={base} page={{ pageTitle: "CV" }} />}
        />
        {collections.map((menuItem) => {
          return (
            <Route
              key={`${menuItem.pageRoute}`}
              path={`${menuItem.pageRoute}`}
              element={<Page base={base} page={menuItem} />}
            />
          );

          // return (
          //   <Route
          //     key={`${menuItem.pageRoute}`}
          //     path={`${menuItem.pageRoute}`}
          //     element={<Page base={base} page={menuItem} />}
          //   />
          // );
        })}

        {installations.map((menuItem) => {
          return (
            <Route
              key={`${menuItem.pageRoute}`}
              path={`${menuItem.pageRoute}`}
              element={<Page base={base} page={menuItem} />}
            />
          );

          // return (
          //   <Route
          //     key={`${menuItem.pageRoute}`}
          //     path={`${menuItem.pageRoute}`}
          //     element={<Page base={base} page={menuItem} />}
          //   />
          // );
        })}

        <Route
          key={"about"}
          path={"about"}
          element={<AboutPage base={base} />}
        />

        <Route path="/" element={<Home splashImages={SplashImage} />} />
      </Routes>
    </HashRouter>
  );
}
