// React
import React, { useState, useEffect } from "react";
import { formatWorkInfoLineRefactored } from "./modules/useWork";

function PageCollections({ collection, base, allWorksById }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [fullScreenIndex, setFullScreenIndex] = useState(undefined);

  useEffect(() => {
    if (Object.keys(allWorksById).length === 0) setIsLoading(true);
    else {
      const tableName = collection.tableName;
      base(tableName)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _collectionWorks = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let workRecord = allWorksById[workId];
            _collectionWorks.push(workRecord);
          });
          setCollectionWorks([..._collectionWorks]);
          setIsLoading(false);
        });
    }
  }, [allWorksById]);

  if (isLoading) return null;

  let imageGrid;
  let fullScreenImage;
  let imageDetails;

  // Show all thumbnails
  if (fullScreenIndex === undefined) {
    imageGrid = (
      <div
        style={{
          display: "flex",
          direction: "row",
          flexWrap: "wrap",
          maxWidth: "100%",
          gap: "50px",
          padding: 40,
        }}
      >
        {collectionWorks.map((work, i) => {
          return (
            <div
              style={{ width: "200px", height: "200px", overflow: "hidden" }}
              onClick={() => {
                setFullScreenIndex(i);
              }}
            >
              <img
                alt="artwork"
                style={{
                  width: "300px",
                  height: "300px",
                  marginLeft: "-50px",
                  marginTop: "-50px",
                  objectFit: "cover",
                }}
                src={work.imageLink}
              />
              {formatWorkInfoLineRefactored(work)}
            </div>
          );
        })}
      </div>
    );
  } else {
    // Show full screen record
    let fullScreenWorkRecord = collectionWorks[fullScreenIndex];

    imageArea = (
      <div>
        <div>
          <div>
            <img
              alt="artwork"
              className="workImage"
              style={{ width: "100%", height: "auto" }}
              src={fullScreenWorkRecord.imageLink}
            />
          </div>
        </div>

        {/* <div
          onClick={() => {
            setFullScreenIndex(undefined);
          }}
          className={"upArrowOnHover"}
          style={{
            position: "absolute",
            top: "0px",
            left: "0px",
            right: "0px",
            height: "50%",
          }}
        ></div>

        <div
          onClick={() => {
            setFullScreenIndex(
              (fullScreenIndex - 1 + collectionWorks.length) %
                collectionWorks.length
            );
          }}
          className={"leftArrowOnHover"}
          style={{
            position: "absolute",
            top: "50%",
            left: "0px",
            width: "50%",
            bottom: "0px",
          }}
        ></div>
        <div
          onClick={() => {
            setFullScreenIndex(
              (fullScreenIndex + 1 + collectionWorks.length) %
                collectionWorks.length
            );
          }}
          className={"rightArrowOnHover"}
          style={{
            position: "absolute",
            top: "50%",
            right: "0px",
            width: "50%",
            bottom: "0px",
          }}
        ></div> */}
      </div>
    );

    imageDetails = (
      <div>
        {formatWorkInfoLineRefactored(fullScreenWorkRecord)}

        <div
          style={{
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
          }}
        >
          <div
            onClick={() => {
              setFullScreenIndex(
                (fullScreenIndex - 1 + collectionWorks.length) %
                  collectionWorks.length
              );
            }}
            style={{ fontWeight: 600 }}
            className={"leftArrowOnHover"}
          >
            prev
          </div>
          <div style={{ marginLeft: 8, marginRight: 8 }}>/</div>
          <div
            onClick={() => {
              setFullScreenIndex(
                (fullScreenIndex + 1 + collectionWorks.length) %
                  collectionWorks.length
              );
            }}
            style={{ fontWeight: 600 }}
            className={"rightArrowOnHover"}
          >
            next
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="collectionPage">
      <div
        style={{
          height: "100%",
          flex: 1,
          display: "flex",
        }}
      >
        <div
          style={{
            padding: 40,
            flex: 1,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "space-between",
            flexDirection: "column",
          }}
        >
          <div>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>
              {collection.title}
            </div>
            {collection.collectionStatement && (
              <div style={{ borderLeft: "1px solid black", padding: 24 }}>
                {collection.collectionStatement}
              </div>
            )}
            {fullScreenIndex !== undefined && (
              <div
                style={{ fontWeight: 600, marginTop: 8 }}
                onClick={() => {
                  setFullScreenIndex(undefined);
                }}
              >
                see all
              </div>
            )}
          </div>
          {imageDetails}
        </div>
      </div>
      <div
        style={{
          height: "100%",
          flex: 2,
          display: "flex",
          overflow: "auto",
          WebkitOverflowScrolling: "touch",
        }}
      >
        {imageArea}
      </div>
    </div>
  );
}

export default PageCollections;
