// React
import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

function MenuInstallations(props) {
  const { installations } = props;

  return (
    <div>
      Installations:
      <div
        style={{
          display: "flex",
          flex: 1,
          alignItems: "space-between",
          flexDirection: "row",
          gap: 40,
          width: "700px",
          flexWrap: "wrap",
        }}
      >
        {installations.map((installation, index) => {
          if (installation.showInMenu) {
            return (
              <Link
                style={{
                  width: "200px",
                  aspectRatio: "3/2",
                  position: "relative",
                  textDecoration: "none",
                }}
                to={installation.route}
                key={`menu-${installation.route}`}
              >
                <img
                  alt="install preview"
                  style={{
                    position: "absolute",
                    top: "0px",
                    bottom: "0px",
                    left: "0px",
                    right: "0px",
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                  }}
                  src={installation.imageLink}
                />
                <div
                  style={{
                    position: "absolute",
                    top: "0px",
                    bottom: "0px",
                    left: "0px",
                    right: "0px",
                    width: "100%",
                    height: "100%",
                    background:
                      "linear-gradient(to right, rgba(0, 0, 0, .8), rgba(0, 0, 0, 0))",
                  }}
                />
                <div
                  style={{
                    display: "flex",
                    flex: 1,
                    alignItems: "space-between",
                    justifyContent: "space-between",
                    flexDirection: "column",
                    height: "100%",
                  }}
                >
                  <div
                    style={{
                      color: "white",
                      zIndex: 2,
                      padding: "16px 16px 4px 16px",
                    }}
                  >
                    <span style={{ fontWeight: 600 }}>
                      {" "}
                      {installation.title}
                    </span>
                    {installation.location && (
                      <>
                        <br />
                        <span>at {installation.location}</span>
                      </>
                    )}
                    {installation.dates && (
                      <>
                        <br />
                        <span>{installation.dates}</span>
                      </>
                    )}
                  </div>
                </div>
              </Link>
            );
          }
        })}
      </div>
    </div>
  );
}

MenuInstallations.propTypes = {
  menuItems: PropTypes.array,
};

export default MenuInstallations;
