// React
import React, { useState, useEffect } from "react";

function PageContact({ base }) {
  const [bio, setBio] = useState("");
  const [bioImage, setBioImage] = useState([]);
  const [showCVLink, setShowCVLink] = useState(false);

  useEffect(() => {
    base("About")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _bio;
        let _bioImage;
        records.forEach(function (record) {
          _bioImage = record.get("Bio Image");
          _bio = record.get("Bio");
          setBio(_bio);
          setBioImage(_bioImage);
          setShowCVLink(record.get("Show CV"));
        });
      });
  }, [base]);

  return (
    <div style={{ marginTop: "64px" }}>
      <div
        style={{
          padding: "64px",
          display: "flex",
          flexDirection: "column",
          gap: 64,
          alignItems: "flex-start",
          justifyContent: "flex-start",
        }}
        className="mobileStack mobilePadding mobileGap"
      >
        <div>
          To recieve the occasional studio update, such as exhibition
          announcements and studio sales,
          <br />
          <a
            target="_blank"
            rel="noreferrer"
            href="https://airtable.com/appFu4bKxcQWPoN69/shrU6lsf5P4fqJlpj"
            style={{ color: "black" }}
          >
            sign up for my mailing list
          </a>
        </div>

        <div>
          I'm also on Instagram,
          <br />
          <a
            target="_blank"
            rel="noreferrer"
            href="https://www.instagram.com/sammytthebrave/"
            style={{ color: "black" }}
          >
            @sammytthebrave
          </a>
        </div>

        <div>
          Reach out to me via email with inquiries, opportunities, questions,
          greetings,
          <br />
          <a
            target="_blank"
            rel="noreferrer"
            href="mailto:srticknor@gmail.com"
            style={{ color: "black" }}
          >
            srticknor@gmail.com
          </a>
        </div>
      </div>
    </div>
  );
}

export default PageContact;
