// React
import React, { useState, useEffect } from "react";
// import { InstagramEmbed } from "react-social-media-embed";

function InstagramEmbed({ page, base }) {
  const [imagesToEmbed, setImagesToEmbed] = useState([]);

  useEffect(() => {
    base(page.pageTitle)
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        const _imagesToEmbed = [];
        records.forEach(function (record) {
          _imagesToEmbed.push(record.instagramID);
        });
        setImagesToEmbed(_imagesToEmbed);
      });
  }, [page.pageTitle]);

  return (
    <div id={"page"}>
      <div id="scrollContent">
        {imagesToEmbed.map((instagramID, index) => {
          return (
            <>
              <blockquote
                class="instagram-media"
                data-instgrm-permalink="https://www.instagram.com/p/CvyLW_QrFys"
                data-instgrm-version="14"
              >
                <div>Placeholder</div>
              </blockquote>
            </>
          );
        })}
      </div>
    </div>
  );
}

export default InstagramEmbed;
