// React
import React, { useState, useEffect } from "react";

function PageAbout({ base }) {
  const [bio, setBio] = useState("");
  const [bioImage, setBioImage] = useState([]);
  const [showCVLink, setShowCVLink] = useState(false);

  useEffect(() => {
    base("About")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _bio;
        let _bioImage;
        records.forEach(function (record) {
          _bioImage = record.get("Bio Image");
          _bio = record.get("Bio");
          setBio(_bio);
          setBioImage(_bioImage);
          setShowCVLink(record.get("Show CV"));
        });
      });
  }, [base]);

  return (
    <div style={{ marginTop: "64px" }}>
      <div
        style={{
          padding: "64px",
          display: "flex",
          flexDirection: "row",
          gap: 64,
          alignItems: "flex-start",
          justifyContent: "flex-start",
        }}
        className="mobileStack mobilePadding"
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            width: "100%",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <img
            alt="Studio portrait of Sam Ticknor"
            style={{ width: "300px", height: "auto" }}
            src={bioImage[0] ? bioImage[0].url : ""}
          />
        </div>
        <div
          className="imageWithTextModuleText"
          style={{
            display: "flex",
            flex: 1,
            flexDirection: "column",
          }}
        >
          {bio && <div style={{ whiteSpace: "pre-wrap" }}>{bio}</div>}
        </div>
      </div>
    </div>
  );
}

export default PageAbout;
