// React
import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

function MenuCollections(props) {
  const { collections, dateCollections } = props;
  const [toggle, setToggle] = useState("collection");

  console.log(dateCollections);
  console.log(dateCollections && Object.keys(dateCollections));

  return (
    <div>
      <div id="menu">
        <div style={{ display: "flex", flexDirection: "row" }}>
          Works by:
          <div
            onClick={() => {
              setToggle("collection");
            }}
          >
            Collection
          </div>
          /
          <div
            onClick={() => {
              setToggle("date");
            }}
          >
            Date
          </div>
        </div>
        {toggle === "collection" &&
          collections.map((collection, index) => {
            if (!collection.showInMenu) return null;
            return (
              <Link
                className="menuOption clickable"
                to={collection.route}
                key={`menu-${collection.route}`}
              >
                <>
                  <span className="menuOptionTitle">{collection.title}</span>
                  {collection.years && (
                    <span className="menuOptionSubtitle">
                      {" — "}
                      {collection.years}
                    </span>
                  )}
                </>
              </Link>
            );
          })}
        {toggle === "date" &&
          Object.keys(dateCollections).map((dateCollectionID, index) => {
            return (
              <Link
                className="menuOption clickable"
                to={collection.route}
                key={`menu-${collection.route}`}
              >
                <>
                  <span className="menuOptionTitle">{dateCollectionID}</span>
                </>
              </Link>
            );
          })}
      </div>
    </div>
  );
}

MenuCollections.propTypes = {
  menuItems: PropTypes.array,
};

export default MenuCollections;
