// React
import React, { useState, useEffect } from "react";
import { ImageFullModule } from "./modules/ImageOnlyModules";

function PageCollections({ page, base }) {
  const [workIds, setWorkIds] = useState(undefined);
  const [works, setWorks] = useState({});

  useEffect(() => {
    // setWorks([]);

    if (document.querySelector("#scrollContent")) {
      document.querySelector("#page").scrollTop = 0;
    }

    const title = page["Title"];
    console.log("TITLE");
    console.log(title);

    base(title)
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        const _workIds = [];
        const _works = {};
        records.forEach(function (record) {
          const workId = record.get("Works")[0];
          _workIds.push(workId);

          base("Works").find(workId, function (err, record) {
            if (err) {
              console.error(err);
              return;
            }
            console.log("Retrieved", record.get("Title"));
          });
          setWorks({ ...works, record });
        });
        setWorkIds(_workIds);
      });
  }, [page.pageTitle]);

  if (works === undefined) return null;

  return (
    <div id={"page"}>
      <div id="scrollContent">
        {workIds.map((work, index) => {
          return (
            <div>{work.fields["Title"]}</div>
            // <ImageFullModule
            //   key={module.moduleTitle}
            //   module={module}
            //   base={base}
            // />
          );
        })}
      </div>
    </div>
  );
}

export default PageCollections;
