// React
import React, { useState, useEffect } from "react";
import { ImageFullWork } from "./modules/ImageOnlyModules";

function PageCollections({ page, base, allworks }) {
  const [collectionWorks, setCollectionWorks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (document.querySelector("#scrollContent")) {
      document.querySelector("#page").scrollTop = 0;
    }
  }, []);

  useEffect(() => {
    if (Object.keys(allworks).length === 0) setIsLoading(true);
    else {
      const title = page["Title"];
      base(title)
        .select({ view: "Grid view" })
        .firstPage(function (err, records) {
          if (err) {
            console.error(err);
            return;
          }
          let _works = [];
          records.forEach(function (record) {
            let workId = record.get("Work")[0];
            let work = allworks[workId];
            _works.push(work);
          });
          setCollectionWorks([..._works]);
          setIsLoading(false);
        });
    }
  }, [allworks]);

  if (isLoading) return null;

  return (
    <div id={"page"}>
      <div id="scrollContent">
        {works.map((work, index) => {
          console.log("MAPPED WORK");
          console.log(index);
          console.log(work);
          return (
            <ImageFullWork key={work.id} work={work} />
            // <div>{work.get("Title")}</div>
          );
        })}
      </div>
    </div>
  );
}

export default PageCollections;
