// React
import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

function MenuCollections(props) {
  const { collections } = props;
  const [toggle, setToggle] = useState("collections");

  return (
    <div>
      <div id="menu">
        <div style={{ display: "flex", flexDirection: "row" }}>
          Works by:
          <div
            onClick={() => {
              setToggle("collections");
            }}
          >
            Collection
          </div>
          /
          <div
            onClick={() => {
              setToggle("collections");
            }}
          >
            Date
          </div>
        </div>
        {collections.map((collection, index) => {
          if (!collection.showInMenu) return null;
          return (
            <Link
              className="menuOption clickable"
              to={collection.route}
              key={`menu-${collection.route}`}
            >
              <>
                <span className="menuOptionTitle">{collection.title}</span>
                {collection.years && (
                  <span className="menuOptionSubtitle">
                    {" — "}
                    {collection.years}
                  </span>
                )}
              </>
            </Link>
          );
        })}
      </div>
    </div>
  );
}

MenuCollections.propTypes = {
  menuItems: PropTypes.array,
};

export default MenuCollections;
