// React
import React, { useState, useEffect } from "react";
import { Routes, HashRouter, Route, Link } from "react-router-dom";

// Helmet
import Helmet from "react-helmet";

// Splash
import SplashImage from "./splash.png";

// Screens
import Home from "./components/Home";
import Page from "./components/Page";
import PageCollections from "./components/PageCollections";

// Style
import "./App.css";
import AboutPage from "./components/AboutPage";
import CVPage from "./components/CVPage";

export default function App() {
  // Site-Wide State
  const [siteTitle, setSiteTitle] = useState("Sam Ticknor");
  const [siteFavicon, setSiteFavicon] = useState("ðŸ”…");
  const [homeSplashImages, setHomeSplashImages] = useState([]);
  const [collections, setCollections] = useState([]);
  const [dateCollections, setDateCollections] = useState({});
  const [allWorksById, setAllWorksById] = useState({});
  const [installations, setInstallations] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // AIRTABLE CONTENT
  var Airtable = require("airtable");
  Airtable.configure({
    endpointUrl: "https://api.airtable.com",
    apiKey:
      "pattHY0czY5YOotHV.e0784aa4aa945b3303da15e14c71b08f80cdf91802cc481c260c880ec890af04", // a read-only key
  });
  const base = Airtable.base("apptr77RaXLy74iuy");

  useEffect(() => {
    // Get information from about table
    base("About")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        let _siteTitle;
        let _siteFavicon;
        let _homeSplashImages;
        records.forEach(function (record) {
          _siteTitle = record.get("Site Title");
          _homeSplashImages = record.get("Splash Images");
          _siteFavicon = record.get("Emoji Favicon");
        });
        setSiteTitle(_siteTitle);
        setSiteFavicon(_siteFavicon);
        setHomeSplashImages(_homeSplashImages);
      });

    // Get all works from works table
    // Put into object that can be referenced by ID
    let _allWorksById = {};
    let _dateCollections = {};
    base("Works")
      .select({
        view: "Grid view",
      })
      .eachPage(
        function page(records, fetchNextPage) {
          records.forEach(function (record) {
            // put work into dictionary indexed by ID
            const workImages = record.get("Image");
            _allWorksById[record.id] = {
              imageLink:
                workImages && workImages.length > 0
                  ? workImages[0].url
                  : undefined,
              videoLink: record.get("Video Link"),
              title: record.get("Title"),
              year: record.get("Year"),
              medium: record.get("Medium"),
              width: record.get("Width"),
              height: record.get("Height"),
              depth: record.get("Depth"),
              description: record.get("Description"),
            };

            // if work belongs to a year collection,
            // put into year dictionary
            // { 2024: [id, id, id], 2023: [id, id, id], 2016: [id, id, id]}}
            let workDate = record.get("Date Collection");
            console.log(workDate);
            if (workDate !== undefined && workDate !== "") {
              if (_dateCollections[workDate]) {
                _dateCollections[workDate].push(record.id);
              } else {
                _dateCollections[workDate] = [record.id];
              }
            }
          });
          fetchNextPage();
        },
        function done(err) {
          console.log(_allWorksById);
          setAllWorksById({ ..._allWorksById });
          setDateCollections({ ..._dateCollections });

          if (err) {
            console.error(err);
            return;
          }
        }
      );
  }, []);

  // Once all works are retrieved,
  // Get menu items from index tables,
  // To build routes and pages
  useEffect(() => {
    base("Collections Index")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        if (err) {
          console.error(err);
          return;
        }
        const _collections = [];
        records.forEach((record) => {
          _collections.push({
            route: record.get("Route"),
            title: record.get("Title"),
            tableName: record.get("Title"),
            showInMenu: record.get("Show In Menu"),
            years: record.get("Years"),
            collectionStatement: record.get("Collection Statement"),
          });
        });
        setCollections([..._collections]);
        setIsLoading(false);
      });

    base("Installations Index")
      .select({ view: "Grid view" })
      .firstPage(function (err, records) {
        console.log(records);
        if (err) {
          console.error(err);
          return;
        }
        const _installations = [];
        records.forEach((record) => {
          const installationImage = record.get("Image");
          _installations.push({
            route: record.get("Route"),
            title: record.get("Title"),
            tableName: record.get("Title"),
            showInMenu: record.get("Show In Menu"),
            link: record.get("Link"),
            dates: record.get("Dates"),
            location: record.get("Location"),
            imageLink:
              installationImage && installationImage.length > 0
                ? installationImage[0].url
                : undefined,
          });
        });
        setInstallations(_installations);
        setIsLoading(false);
      });
  }, [allWorksById]);

  // MARKUP
  if (isLoading) return null;
  return (
    <HashRouter basename="/" key={window.location.pathname}>
      <Helmet>
        <title>{siteTitle}</title>
        <link
          rel="icon"
          href={`data:image/svg+xml,<svg xmlns=%22http://www.w3.org/2000/svg%22 viewBox=%220 0 100 100%22><text y=%22.9em%22 font-size=%2290%22>${siteFavicon}</text></svg>`}
          sizes="16x16"
        />
      </Helmet>

      {/* NAV BAR */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          height: "64px",
          background: "#f8f8f8",
          display: "flex",
          alignItems: "center",
          justifyContent: "flex-start",
          gap: "32px",
          padding: "0px 32px",
          zIndex: 101,
        }}
      >
        <Link
          style={{
            textDecoration: "none",
            fontWeight: 600,
            color: "black",
            fontSize: "18px",
          }}
          to="/"
        >
          Sam Ticknor
        </Link>
        <Link
          style={{
            textDecoration: "none",
            color: "black",
          }}
          to="/"
        >
          Works
        </Link>
        <Link
          style={{
            textDecoration: "none",
            color: "black",
          }}
          to="/about"
        >
          About
        </Link>
      </div>

      <Routes>
        <Route
          path="/CV"
          element={<CVPage base={base} page={{ pageTitle: "CV" }} />}
        />

        {Object.keys(dateCollections).map((dateCollectionID) => {
          let collection = dateCollections(dateCollectionID);
          return (
            <Route
              key={`${collection.route}`}
              path={`${collection.route}`}
              element={
                <PageCollections
                  base={base}
                  collection={collection}
                  allWorksById={allWorksById}
                />
              }
            />
          );
        })}

        {collections.map((collection) => {
          return (
            <Route
              key={`${collection.route}`}
              path={`${collection.route}`}
              element={
                <PageCollections
                  base={base}
                  collection={collection}
                  allWorksById={allWorksById}
                />
              }
            />
          );
        })}

        {installations.map((installation) => {
          return (
            <Route
              key={`${installation.route}`}
              path={`${installation.route}`}
              element={
                <Page
                  base={base}
                  page={installation}
                  allWorksById={allWorksById}
                />
              }
            />
          );
        })}

        <Route
          key={"about"}
          path={"about"}
          element={<AboutPage base={base} />}
        />

        <Route
          path="/"
          element={
            <Home
              splashImages={SplashImage}
              collections={collections}
              dateCollections={dateCollections}
              installations={installations}
              isLoading={isLoading}
            />
          }
        />
      </Routes>
    </HashRouter>
  );
}
